declare global {
  interface Window {
    google?: {
      accounts: {
        id: {
          initialize: (config: any) => void;
          renderButton: (element: HTMLElement, config: any) => void;
          cancel: () => void;
        };
      };
    };
  }
}

interface GoogleAuthButtonProps {
  onSuccess?: () => void;
  className?: string;
}

export function GoogleAuthButton({ onSuccess, className }: GoogleAuthButtonProps) {
  const { loginWithGoogle, isLoggingIn } = useAuth();
  const { toast } = useToast();

  useEffect(() => {
    // Load Google script if not already loaded
    if (!window.google) {
      const script = document.createElement("script");
      script.src = "https://accounts.google.com/gsi/client";
      script.async = true;
      script.defer = true;
      document.head.appendChild(script);

      script.onload = initializeGoogle;
    } else {
      initializeGoogle();
    }

    return () => {
      // Cancel Google auth UI on unmount
      if (window.google) {
        window.google.accounts.id.cancel();
      }
    };
  }, []);

  const initializeGoogle = () => {
    if (window.google) {
      window.google.accounts.id.initialize({
        client_id: import.meta.env.VITE_GOOGLE_CLIENT_ID,
        callback: handleGoogleSuccess,
      });
    }
  };

  const handleGoogleSuccess = async (response: any) => {
    try {
      await loginWithGoogle(response.credential);
      toast({
        title: "Ã‰xito",
        description: "Has iniciado sesiÃ³n correctamente",
      });
      onSuccess?.();
    } catch (error: any) {
      toast({
        variant: "destructive",
        title: "Error",
        description: error.message || "Failed to authenticate",
      });
    }
  };

  return (
    <div id="google-auth-button" className={className}>
      {/* Google Sign-In button will be rendered here by Google SDK */}
    </div>
  );
}

import { Progress } from "@/components/ui/progress";
import { Card, CardContent } from "@/components/ui/card";
import { Loader2 } from "lucide-react";

interface GenerationProgressProps {
  logs: string[];
  isLoading: boolean;
  language?: "es" | "en";
  className?: string;
}

export function GenerationProgress({
  logs,
  isLoading,
  language = "es",
  className,
}: GenerationProgressProps) {
  const currentStep = Math.min(logs.length, isLoading ? logs.length : logs.length - 1);
  const progress = (currentStep / Math.max(logs.length, 1)) * 100;

  return (
    <Card className={className}>
      <CardContent className="pt-6 space-y-4">
        {/* Progress bar */}
        <div>
          <Progress value={progress} className="h-2" />
        </div>

        {/* Log entries */}
        <div className="space-y-2">
          {logs.map((log, index) => {
            const isCompleted = index < currentStep;
            const isCurrent = index === currentStep && isLoading;

            return (
              <div
                key={index}
                className={`flex items-center gap-2 p-2 rounded transition-colors ${
                  isCompleted || isCurrent
                    ? "bg-green-50 dark:bg-green-900/20 text-green-900 dark:text-green-200"
                    : "text-muted-foreground"
                }`}
              >
                {isCurrent ? (
                  <Loader2 className="w-4 h-4 animate-spin text-orange-500" />
                ) : isCompleted ? (
                  <div className="w-4 h-4 bg-green-500 rounded-full" />
                ) : (
                  <div className="w-4 h-4 border-2 border-gray-300 rounded-full" />
                )}
                <span className="text-sm">{log}</span>
              </div>
            );
          })}
        </div>

        {/* Current progress percentage */}
        {isLoading && (
          <div className="text-center text-xs text-muted-foreground">
            {Math.round(progress)}%
          </div>
        )}
      </CardContent>
    </Card>
  );
}

import { GoogleGenAI } from "@google/genai";
import { log } from "./index";

// Gemini API configuration with fallback models - ordered by capability
const GEMINI_MODELS = [
  "gemini-3-flash-preview",    // Most intelligent - All tools
  "gemini-2.5-pro",             // Advanced thinking - All tools + Maps
  "gemini-2.5-flash",           // Best price-performance - All tools
  "gemini-2.5-flash-lite",      // Ultra fast - All tools, optimized
] as const;

type GeminiModel = (typeof GEMINI_MODELS)[number];

interface GeminiRequest {
  userPrompt: string;
  systemPrompt?: string;
  includeThinking?: boolean;
  temperature?: number;
  maxTokens?: number;
  imageData?: {
    mimeType: "image/jpeg" | "image/png" | "image/gif" | "image/webp";
    data: string; // base64 encoded
  };
}

interface GeminiResponse {
  text: string;
  model: GeminiModel;
  thinking?: string;
  usageStats?: {
    inputTokens?: number;
    outputTokens?: number;
  };
}

// Initialize Gemini client with API key
const initializeGeminiClient = (): GoogleGenAI => {
  const apiKey = process.env.GEMINI_API_KEY;
  if (!apiKey) {
    throw new Error(
      "GEMINI_API_KEY environment variable not set. Get it from https://aistudio.google.com/apikey"
    );
  }

  return new GoogleGenAI({
    apiKey,
  });
};

let geminiClient: GoogleGenAI | null = null;

const getGeminiClient = (): GoogleGenAI => {
  if (!geminiClient) {
    geminiClient = initializeGeminiClient();
  }
  return geminiClient;
};

export async function generateGeminiResponse(
  request: GeminiRequest
): Promise<GeminiResponse> {
  const client = getGeminiClient();
  let lastError: Error | null = null;

  for (const model of GEMINI_MODELS) {
    try {
      log(`Attempting Gemini with model: ${model}`, "gemini-service");

      // Build the content parts
      const parts: any[] = [];

      // Add image if provided
      if (request.imageData) {
        parts.push({
          inlineData: {
            mimeType: request.imageData.mimeType,
            data: request.imageData.data,
          },
        });
      }

      // Add user prompt
      const finalPrompt = request.systemPrompt
        ? `${request.systemPrompt}\n\n${request.userPrompt}`
        : request.userPrompt;

      parts.push({
        text: finalPrompt,
      });

      // Build request config with thinking support
      const requestConfig: any = {
        model,
        contents: [
          {
            role: "user",
            parts,
          },
        ],
        generationConfig: {
          temperature: request.temperature ?? 0.7,
          maxOutputTokens: request.maxTokens ?? 2000,
        },
      };

      // Add thinking config if requested and model supports it
      if (request.includeThinking) {
        // All current models support thinking (2.0-flash has experimental)
        requestConfig.thinking = {
          type: "ENABLED",
          budgetTokens: 5000,
        };
      }

      // Add tools based on model capabilities - all current models have these tools
      const tools: any[] = [
        { googleSearchRetrieval: {} },
        { codeExecution: {} },
      ];

      // Add Google Maps for Pro models (if supported)
      if (model.includes("pro")) {
        tools.push({ googleMaps: {} });
      }

      requestConfig.tools = tools;

      const response = await client.models.generateContent(requestConfig);

      const text = response.candidates?.[0]?.content?.parts
        ?.map((p: any) => p.text || "")
        .join("") || "";
      if (!text) {
        throw new Error(`No text response from ${model}`);
      }

      // Extract thinking if available
      let thinking: string | undefined;
      if (request.includeThinking && response.candidates) {
        for (const candidate of response.candidates) {
          if (candidate.content?.parts) {
            for (const part of candidate.content.parts) {
              if ((part as any).thinking) {
                thinking = (part as any).thinking;
                break;
              }
            }
          }
        }
      }

      log(`Successfully generated response with ${model}`, "gemini-service");

      return {
        text,
        model,
        thinking,
        usageStats: {
          inputTokens: response.usageMetadata?.promptTokenCount,
          outputTokens: response.usageMetadata?.candidatesTokenCount,
        },
      };
    } catch (error: any) {
      log(
        `Model ${model} failed: ${error.message || "Unknown error"}`,
        "gemini-service"
      );
      lastError = error;
      // Continue to next model
    }
  }

  throw (
    lastError ||
    new Error(
      "All Gemini models failed. Please check your API key and rate limits."
    )
  );
}

// Stream version for real-time responses
export async function* streamGeminiResponse(
  request: GeminiRequest
): AsyncGenerator<string, void, unknown> {
  const client = getGeminiClient();
  let lastError: Error | null = null;

  for (const model of GEMINI_MODELS) {
    try {
      log(`Attempting stream with model: ${model}`, "gemini-service");

      const parts: any[] = [];

      if (request.imageData) {
        parts.push({
          inlineData: {
            mimeType: request.imageData.mimeType,
            data: request.imageData.data,
          },
        });
      }

      const finalPrompt = request.systemPrompt
        ? `${request.systemPrompt}\n\n${request.userPrompt}`
        : request.userPrompt;

      parts.push({ text: finalPrompt });

      const requestConfig: any = {
        model,
        contents: [{ role: "user", parts }],
        generationConfig: {
          temperature: request.temperature ?? 0.7,
          maxOutputTokens: request.maxTokens ?? 2000,
        },
      };

      if (request.includeThinking) {
        // All current models support thinking
        requestConfig.thinking = {
          type: "ENABLED",
          budgetTokens: 5000,
        };
      }

      // Add tools based on model capabilities - all current models have these tools
      const tools: any[] = [
        { googleSearchRetrieval: {} },
        { codeExecution: {} },
      ];

      // Add Google Maps for Pro models
      if (model.includes("pro")) {
        tools.push({ googleMaps: {} });
      }

      requestConfig.tools = tools;

      const stream = await client.models.generateContentStream(requestConfig);

      for await (const chunk of stream) {
        const text = chunk.candidates?.[0]?.content?.parts
          ?.map((p: any) => p.text || "")
          .join("");

        if (text) {
          yield text;
        }
      }

      return;
    } catch (error: any) {
      log(`Stream with ${model} failed: ${error.message}`, "gemini-service");
      lastError = error;
    }
  }

  throw lastError || new Error("All streaming models failed");
}

export function isValidGeminiModel(model: string): model is GeminiModel {
  return GEMINI_MODELS.includes(model as GeminiModel);
}

export function getAvailableModels(): readonly GeminiModel[] {
  return GEMINI_MODELS;
}

